import React, { useEffect, useState } from 'react'
import './css/Inventory.css'

export default function Inventory() {

    const [cars, setCars] = useState([]);
    const [parts, setParts] = useState([]);
    const [misc, setMisc] = useState([])
    const [showPartsInCars, setShowPartsInCars] = useState(false);
    const [option, setOption] = useState("cars")

    useEffect(() => {
        fetch("http://localhost:3080/getInventory",  {
            method: "POST",
            headers: {
                'Content-Type': 'application/json'
            },
            body: {
                token: localStorage.getItem("token"),
                option: option
        }})
        .then(res => {
            if(option === "cars") {
                setCars(res.data)
            }
            else if(option === "parts") {
                setParts(res.data)
            }
            else if(option === "misc") {
                setMisc(res.data)
            }
        })
    }, [option]);


    function getInventory() {
        if(option === "cars") {
            return (
                <div>
                    {cars.map(car => 
                        <div>
                            <p>{car.name}</p>
                            <p>{car.price}</p>
                            <p>{car.rarity}</p>
                        </div>
                        )}
                </div>
            )
        }

        if(option === "parts") {
            return (
                <div>
                    <input type="checkbox" checked={false} onChange={e => setShowPartsInCars(e.target.checked)}/>
                    <label>Also show parts used in cars?</label>
                    {parts.map(part => 
                        <div>
                            <p>{part.name}</p>
                            <p>{part.price}</p>
                            <p>{part.rarity}</p>                            
                        </div>
                        )}
                </div>
            )
        }

        if(option === "misc") {
            return (
                <div>
                    {misc.map(misc => 
                        <div>
                            <p>Misc</p>
                        </div>
                    )}
                </div>
            )
        }

    }


    return(
        <div>
            <h2 class="inventoryHeader">Inventory</h2>
            <button onClick={() => setOption("cars")}>Cars</button>
            <button onClick={() => setOption("parts")}>Parts</button>
            <button onClick={() => setOption("misc")}>Misc</button>
            {getInventory()}
        </div>

    )
}
