import React, { useEffect, useState } from 'react'
import './css/Inventory.css'

export default function Inventory() {

    const [cars, setCars] = useState(null);
    const [parts, setParts] = useState(null);
    const [option, setOption] = useState("cars")

    useEffect(() => {
        fetch("http://localhost:3080/getInventory",  {
            method: "POST",
            headers: {
                'Content-Type': 'application/json'
            },
            body: {
                token: localStorage.getItem("token"),
                option: option
            }.then(data => {
                if(option === "cars") {
                    setCars(data)
                }
                else if(option === "parts") {
                    setParts(data)
                }
                else if(option === "misc") {
                    setMisc(data)
                }
            })
        })
    }, [option]);

    if(option === "cars") {
        return (
            <div>
                <button onClick={() => setOption("cars")}>Cars</button>
                <button onClick={() => setOption("parts")}>Parts</button>
                <button onClick={() => setOption("misc")}>Misc</button>
                <p>You chose cars</p>
            </div>
        )
    }

    if(option === "parts") {
        return (
            <div>
                <button onClick={() => setOption("cars")}>Cars</button>
                <button onClick={() => setOption("parts")}>Parts</button>
                <button onClick={() => setOption("misc")}>Misc</button>
                <p>You chose parts</p>
            </div>
        )
    }

    if(option === "misc") {
        return (
            <div>
                <button onClick={() => setOption("cars")}>Cars</button>
                <button onClick={() => setOption("parts")}>Parts</button>
                <button onClick={() => setOption("misc")}>Misc</button>
                <p>You chose misc</p>
            </div>
        )
    }


    return(
        <div>
            <h2 class="inventoryHeader">Inventory</h2>
        </div>

    )
}
