    }

    return(
        <div>
            <h2>Dashboard boiii</h2>
            <p>I would very much like to see all the active bids that i am allowed to see</p>
            {bidButtons}
            <button onClick={handleClick}>Inventory</button>
            <button onClick={handleClick}>Admin</button>
        </div>
    )
}
